package first;

public class Arith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x=10, y=2;

System.out.println( "Add="+(x+y));
System.out.println("Sub="+(x-y));
System.out.println("Multi="+(x*y));
System.out.println("Div="+(x/y));
System.out.println("Mod="+(x%y));
	}
	

}
